      email
    });

    if (!user) {
      return res.status(401).send({
        estado: "error",
        msg: "Credenciales NO válidas!!!"
      });
    }

    const passOK = await compare(password, user.password);

    if (passOK) {
      const token = sign({
        usuario: user.email,
